package com.jpmc.questions;

/**
 * Substring problems
 */

public class SubstringProblem {

    /**
     * Given a binary string, count number of substrings that start and end with 1.
     * For example, if the input string is “00100101”, then there are three substrings “1001”, “100101” and “101”.
     */

    public int noOfSubString(String input){
        int i=0;
        int count=0;
        while(i < input.length()){
            if(input.charAt(i) == '1') {
                for (int j = i+1; j < input.length(); j++) {
                    if (input.charAt(j) == '1') {
                        count++;
                    }
                }
            }
            i++;
        }
        return count;
    }

    /**
     * Given a binary string s, return the number of substrings with all characters 1's.
     * Since the answer may be too large, return it modulo 10^9 + 7.
     */

    public int numSub(String s) {
        int i=0;
        int count=0;

        while(i < s.length()) {
            if(s.charAt(i) == '1') {
                count++;
                for (int j = i + 1; j < s.length(); j++) {
                    if (s.charAt(j) == '1') {
                        count++;
                    } else {
                        break;
                    }
                }
            }
            i++;
        }
        return count % (1000000007);
    }


    /**
     * Efficient solution- Given a binary string s, return the number of substrings with all characters 1's.
     * Since the answer may be too large, return it modulo 10^9 + 7.
     */

    public int numSubstring(String s) {
        int i=0;
        int count=0;
        int prevCount=0;
        boolean isPreviousOne = false;

        while(i < s.length()) {
            if(s.charAt(i) == '1') {
                if(isPreviousOne) {
                    prevCount++;
                    count = count + prevCount;
                }
                count++;
                isPreviousOne = true;
            } else {
                isPreviousOne = false;
                prevCount = 0;
            }
            i++;

        }
        return count;
    }

    public static void main(String[] args){
        //System.out.println(new SubstringProblem().numSubstring("1111"));
        //System.out.println(new SubstringProblem().noOfSubString("00100101"));
        //System.out.println(new SubstringProblem().numSub(""));


    }

}

